SELECT uu.nickname, oo.order_id, oo.order_sn, oo.pay_amount, oo.pay_type, oo.order_status, oo.order_type, oo.create_time, oo.payment_time, oo.trans_id, oo.cpy_type, ooi.product_name, ooi.product_price, ooi.product_quantity FROM oms_order oo LEFT JOIN oms_order_item ooi ON oo.order_id = ooi.order_id LEFT JOIN ums_user uu ON oo.user_id = uu.user_id
AND oo.delete_status = #{params.deleteStatus}
