mapper.sql:
IFNULL(ROUND(
     6378.138*2*ASIN(
         SQRT(
             POW(
                 SIN(( #{mapQuery.lat}*PI()/180 - latitude*PI()/180)/2),2) +
        COS( #{mapQuery.lat}*PI()/180) *
        COS( latitude*PI()/180) *
        POW( SIN(( #{mapQuery.lng}*PI()/180-longitude*PI()/180)/2),2
        )
        )
        )*1000,2
     ),
0) AS fpp_distance

mapper.select:
SELECT fpp.flash_promotion_pdt_id, fpp.flash_promotion_id, fpp.flash_promotion_sn_id, fpp.flash_promotion_price, SUM(fpp.flash_promotion_count) flash_promotion_count , fpp.flash_promotion_limit, fpp.sort, pp.product_id, pp.product_name, pp.pic, pp.price, pp.partner_id, up.latitude lat, up.longitude lng,
from sms_flash_promotion_product fpp left join pms_product pp on pp.product_id = fpp.product_id LEFT JOIN ums_partner up ON pp.partner_id = up.partner_id
fpp.flash_promotion_id = #{flashPromotionId}
and fpp.user_id is null
and fpp.user_id > 0
and fpp.flash_promotion_pdt_id < #{flashPromotionPdtId}
and fpp.flash_product_status = 2
GROUP BY fpp.`product_id`,fpp.`flash_promotion_price`,fpp.`cpy_type` ORDER BY fpp.flash_promotion_pdt_id DESC
limit 20;

SELECT DATE_FORMAT(
               end_time,
               '%Y-%m-%d %T'
           ) end_time
from sms_flash_promotion
where flash_promotion_id = #{flashPromotionId};
