mapper.select:
select flash_promotion_pdt_id,d.sub_title,a.flash_promotion_price,d.pic,b.nickname publisherName,b.head_portrait publisher_pic, c.nickname,c.head_portrait nick_pic from sms_flash_promotion_product a inner join ums_user b on b.user_id=a.user_id inner join ums_user c on c.user_id=a.publisher_id inner join pms_product d on d.product_id=a.product_id inner join pms_sku_stock e on e.sku_stock_id=a.sku_stock_id where 1=1
and flash_promotion_pdt_id < #{integralId}
order by a.create_time desc
limit 1;

select a.*, d.sub_title, d.pic, e.price, pvc.`is_status`, d.`partner_id` from sms_flash_promotion_product a LEFT JOIN pms_product d on d.product_id=a.product_id LEFT JOIN pms_sku_stock e on e.sku_stock_id=a.sku_stock_id LEFT JOIN pms_verification_code pvc ON d.`partner_id` =pvc.`partner_id` where a.user_id=#{userId} and a.`cpy_type` = #{cpyType}
and a.flash_promotion_pdt_id < #{integralId}
order by a.flash_promotion_pdt_id desc
limit 6;

select a.*, d.product_name, d.sub_title, d.purchase_note, d.pic , up.partner_id, up.phone, CONCAT(up.province,up.region,up.address) AS fullAddress, (SELECT sfp.`start_time` FROM `sms_flash_promotion` AS sfp WHERE sfp.`cpy_type` = a.`cpy_type` AND NOW() < sfp.`start_time` AND sfp.`is_publish_status` = 1 ORDER BY sfp.`start_time` ASC LIMIT 1 ) AS `start_time`, a.flash_promotion_price as `price` FROM sms_flash_promotion_product a INNER JOIN pms_product d ON d.product_id=a.product_id INNER JOIN ums_partner up ON d.partner_id = up.partner_id
and a.flash_promotion_pdt_id = #{integralId} and a.user_id=#{userId}
order by a.create_time desc

SELECT sfpp.`flash_promotion_pdt_id`, SUM(sfpp.`flash_promotion_count`) AS flash_promotion_count, sfpp.`flash_promotion_price`, sfpp.user_id FROM `sms_flash_promotion_product` AS sfpp LEFT JOIN `sms_flash_promotion` AS sfp ON sfp.`flash_promotion_id` = sfpp.`flash_promotion_id` WHERE sfpp.`product_id` = #{productId} AND sfpp.`flash_promotion_count` > 0 AND sfpp.`is_publish_status` = 1 AND sfpp.`flash_product_status` = 2 AND sfpp.`flash_promotion_price` = #{price} AND sfpp.`flash_promotion_id` = #{flashPromotionId}
AND NOW() BETWEEN sfp.`start_time` and sfp.`end_time`
GROUP BY sfpp.`product_id` , sfpp.`flash_promotion_price` ORDER BY sfpp.`flash_promotion_pdt_id` ASC

SELECT SUM(flash_promotion_origin_price) FROM `sms_flash_promotion_product` AS sfpp WHERE sfpp.`flash_promotion_id` IN(

        #{prId}

)
AND sfpp.`user_id` IN (

    #{uid}

)

mapper.update:
update sms_flash_promotion_product
set  flash_promotion_count =
CASE

    when flash_promotion_pdt_id = #{key} then flash_promotion_count - #{value}

END
where flash_promotion_pdt_id IN(

    #{key}

)

update sms_flash_promotion_product
set  flash_promotion_count =
CASE

    when flash_promotion_pdt_id = #{key} then flash_promotion_count + #{value}

END
where flash_promotion_pdt_id IN(

    #{key}

)

update sms_flash_promotion_product
SET flash_promotion_count = 0 , flash_product_status = 1
where flash_promotion_pdt_id = #{flashPromotionPdtId}

update sms_flash_promotion_product
SET  flash_product_status = 4
where flash_promotion_id = #{flashPromotionId}

mapper.select:
SELECT sfpp.`flash_promotion_pdt_id`, sfpp.`product_id`, sfpp.`flash_promotion_origin_count`, sfpp.`flash_promotion_price`, sfpp.`publisher_id`, sfpp.`flash_promotion_origin_price`, sfpp.`cpy_type` FROM `sms_flash_promotion_product` AS sfpp WHERE sfpp.`flash_promotion_pdt_id` IN (
#{key}
) AND sfpp.`is_publish_status` = 1

SELECT flash_promotion_pdt_id, flash_promotion_price, cpy_type, flash_product_status, product_id, user_id, order_id, expire_time FROM sms_flash_promotion_product WHERE user_id = #{userId}
AND cpy_type = #{cpyType}
ORDER BY flash_promotion_pdt_id DESC LIMIT #{pageSize};

SELECT flash_promotion_pdt_id, flash_promotion_price, cpy_type, flash_product_status, product_id, user_id, order_id, expire_time FROM sms_flash_promotion_product
flash_promotion_pdt_id < #{flashPromotionPdtId}
AND user_id = #{userId} AND cpy_type = #{cpyType}
ORDER BY flash_promotion_pdt_id DESC LIMIT 20;
